package require java
java::import -package java.util HashMap Map
java::import -package java.util ArrayList
proc getCount {graph_id object_type domain_id} {
set map [java::new HashMap]
$map put "nodeType" "DATA_NODE"
$map put "objectType" $object_type
$map put "subject" $domain_id
$map put "status" "Live"
set search_criteria [create_search_criteria $map]
set search_response [getNodesCount $graph_id $search_criteria]
set check_error [check_response_error $search_response]
if {$check_error} {
return 0
} else {
set count [get_resp_value $search_response "count"]
return $count
}
}
proc getContentCount {graph_id domain_id} {
set map [java::new HashMap]
$map put "nodeType" "DATA_NODE"
$map put "objectType" "Content"
set domains [java::new ArrayList]
$domains add $domain_id
$map put "domain" $domains
$map put "status" "Live"
set contentTypes [java::new ArrayList]
$contentTypes add "Story"
$contentTypes add "Worksheet"
$contentTypes add "Game"
$contentTypes add "Collection"
$map put "contentType" $contentTypes
set search_criteria [create_search_criteria $map]
set search_response [getNodesCount $graph_id $search_criteria]
set check_error [check_response_error $search_response]
if {$check_error} {
return 0
} else {
set count [get_resp_value $search_response "count"]
return $count
}
}
set object_type "Domain"
set graph_id "domain"
set resp_get_node [getDataNode $graph_id $domain_id]
set check_error [check_response_error $resp_get_node]
if {$check_error} {
return $resp_get_node;
} else {
set returnFields false
set is_fieldList_null [java::isnull $fields]
if {$is_fieldList_null == 0} {
set returnFields true
}
set result_map [java::new HashMap]
java::try {
set graph_node [get_resp_value $resp_get_node "node"]
set resp_def_node [getDefinition $graph_id $object_type]
set def_node [get_resp_value $resp_def_node "definition_node"]
if {$returnFields} {
set resp_object [convert_graph_node $graph_node $def_node $fields]
} else {
set resp_object [convert_graph_node $graph_node $def_node]
}
set dimension_count [getCount $graph_id "Dimension" $domain_id]
set concept_count [getCount $graph_id "Concept" $domain_id]
set misconception_count [getCount $graph_id "Misconception" $domain_id]
set method_count [getCount $graph_id "Method" $domain_id]
set content_count [getContentCount $graph_id $domain_id]
$resp_object put "dimension_count" $dimension_count
$resp_object put "concept_count" $concept_count
$resp_object put "misconception_count" $misconception_count
$resp_object put "method_count" $method_count
$resp_object put "content_count" $content_count
$result_map put "domain" $resp_object
} catch {Exception err} {
$result_map put "error" [$err getMessage]
}
set response_list [create_response $result_map]
return $response_list
}

package require java
java::import -package java.util ArrayList List
java::import -package java.util HashMap Map
java::import -package com.ilimi.graph.dac.model Filter
java::import -package com.ilimi.graph.dac.model Node
java::import -package com.ilimi.graph.dac.model Relation
proc getNodeRelations {graphId nodeId} {
set response [getDataNode $graphId $nodeId]
set check_error [check_response_error $response]
if {$check_error} {
java::throw [java::new Exception "Error response from getDataNode"]
}
set node [get_resp_value $response "node"]
set outRelations [java::prop $node "outRelations"]
return $outRelations
}
proc relationsExist {relations} {
set exist false
set hasRelations [java::isnull $relations]
if {$hasRelations == 0} {
set relationsSize [$relations size]
if {$relationsSize > 0} {
set exist true
}
}
return $exist
}
proc getNodeProperty {graphId nodeId property} {
set response [getDataNode $graphId $nodeId]
set check_error [check_response_error $response]
if {$check_error} {
java::throw [java::new Exception "Error response from getDataNode"]
}
set node [get_resp_value $response "node"]
set propValue [java::prop $node $property]
return $propValue
}
proc getConcepts {graphId nodeId} {
set concepts [java::new ArrayList]
set relations [getNodeRelations $graphId $nodeId]
set hasRelations [relationsExist $relations]
if {$hasRelations} {
java::for {Relation relation} $relations {
if {[java::prop $relation "endNodeObjectType"] == "Concept"} {
$concepts add [java::prop $relation "endNodeId"]
}
}
}
return $concepts
}
set resultMap [java::new HashMap]
java::try {
set concepts [getConcepts $graphId $itemId]
set itemMetadata [getNodeProperty $graphId $itemId "metadata"]
set maxScore [$itemMetadata get "max_score"]
if {[$concepts size] == 0} {
set concepts [getConcepts $graphId $contentId]
}
} catch {Exception err} {
puts [$err getMessage]
$resultMap put "error" [$err getMessage]
}
$resultMap put "concepts" $concepts
$resultMap put "maxScore" $maxScore
set responseList [create_response $resultMap]
return $responseList

package require java
java::import -package java.util ArrayList List
java::import -package java.util HashMap Map
java::import -package com.ilimi.graph.dac.model Filter
java::import -package com.ilimi.graph.dac.model Node
java::import -package com.ilimi.graph.dac.model Relation
proc getNode {graphId nodeId} {
set response [getDataNode $graphId $nodeId]
set check_error [check_response_error $response]
if {$check_error} {
java::throw [java::new Exception "Error response from getDataNode"]
}
set node [get_resp_value $response "node"]
return $node
}
proc relationsExist {relations} {
set exist false
set hasRelations [java::isnull $relations]
if {$hasRelations == 0} {
set relationsSize [$relations size]
if {$relationsSize > 0} {
set exist true
}
}
return $exist
}
proc getNodeMetadata {node property} {
set metadata [java::prop $node "metadata"]
set propValue [$metadata get $property]
return $propValue
}
proc getNodeRelationIds {node relationType property} {
set relationIds [java::new ArrayList]
set outRelations [java::prop $node "outRelations"]
set hasRelations [relationsExist $outRelations]
if {$hasRelations} {
java::for {Relation relation} $outRelations {
if {[java::prop $relation "endNodeObjectType"] == $relationType} {
$relationIds add [java::prop $relation $property]
}
}
}
return $relationIds
}
proc getSetMembers {graphId setId} {
set response [getDataNode $graphId $nodeId]
set check_error [check_response_error $response]
if {$check_error} {
java::throw [java::new Exception "Error response from getSetMembers"]
}
set members [get_resp_value $response "members"]
return $members
}
proc getItem {graphId itemId} {
set node [getNode $graphId $nodeId]
set item [java::prop $node "metadata"]
$item put "tags" [java::prop $node "tags"]
set concepts [getNodeRelationIds $node "Concept" "endNodeId"]
$item put "concepts" $concepts
return $item
}
set graphId "domain"
set resultMap [java::new HashMap]
set items [java::new ArrayList]
java::try {
set content [getNode $graphId $contentId]
set itemSetIds [getNodeRelationIds $content "ItemSet" "endNodeId"]
if {[$itemSetIds size] > 0} {
java::for {String itemSetId} $itemSetIds {
set itemIds [getSetMembers $graphId $itemSetId]
if {[$itemIds length] > 0} {
java::for {String itemId} $itemIds {
$items add [getItem $graphId $itemId]
}
}
}
}
} catch {Exception err} {
puts [$err getMessage]
$resultMap put "error" [$err getMessage]
}
$resultMap put "items" $items
set responseList [create_response $resultMap]
return $responseList

package require java
java::import -package java.util HashMap Map
java::import -package java.util ArrayList List
proc proc_isNotNull {value} {
set exist false
java::try {
set hasValue [java::isnull $value]
if {$hasValue == 0} {
set exist true
}
} catch {Exception err} {
set exist false
}
return $exist
}
proc proc_getFirstElement {input_list} {
set listNotNull [proc_isNotNull $input_list]
if {$listNotNull} {
set arr_instance [java::instanceof $input_list {String[]}]
if {$arr_instance == 1} {
set array [java::cast {String[]} $input_list]
set listSize [$array length]
if {$listSize > 0} {
set word [$array get 0]
return $word
} else {
return [java::new String "English"]
}
} else {
return [$input_list toString]
}
} else {
return [java::new String "English"]
}
}
proc proc_updateLanguageCode {resp_object graph_node} {
set objectNotNull [proc_isNotNull $resp_object]
if {$objectNotNull} {
set languageCode [$resp_object get "languageCode"]
set languageCodeNotNull [proc_isNotNull $languageCode]
if {!$languageCodeNotNull} {
set node_metadata [java::prop $graph_node "metadata"]
set language [$node_metadata get "language"]
set languageVal [proc_getFirstElement $language]
set language_map [java::new HashMap]
$language_map put "english" "en"
$language_map put "telugu" "te"
$language_map put "hindi" "hi"
$language_map put "kannada" "ka"
$language_map put "tamil" "ta"
$language_map put "marathi" "mr"
$language_map put "bengali" "bn"
$language_map put "gujarati" "gu"
$language_map put "odia" "or"
$language_map put "assamese" "as"
set lang_lc [[java::new String $languageVal] toLowerCase]
set langCode [$language_map get $lang_lc]
set langCodeNotNull [proc_isNotNull $langCode]
if {$langCodeNotNull} {
$resp_object put "languageCode" $langCode
} else {
$resp_object put "languageCode" "en"
}
}
}
}
set isEditMode 0
set imageMode 0
set object_type "Content"
set graph_id "domain"
set content_image_id ${content_id}.img
set is_mode_null [java::isnull $mode]
set resp_get_node [java::null]]
if {($is_mode_null == 0) && ([$mode toString] == "edit")} {
set resp_get_node [getDataNode $graph_id $content_image_id]
set check_error [check_response_error $resp_get_node]
if {$check_error} {
set isEditMode 1
set resp_get_node [getDataNode $graph_id $content_id]
} else {
set imageMode 1
}
} else {
set resp_get_node [getDataNode $graph_id $content_id]
}
set check_error [check_response_error $resp_get_node]
if {$check_error} {
return $resp_get_node;
} else {
set returnFields false
set is_fieldList_null [java::isnull $fields]
if {$is_fieldList_null == 0} {
set returnFields true
}
set graph_node [get_resp_value $resp_get_node "node"]
set metadata [java::prop $graph_node "metadata"]
set status_val [$metadata get "status"]
set status_val_str [java::new String [$status_val toString]]
set isLiveState [$status_val_str equalsIgnoreCase "Live"]
set isFlaggedState [$status_val_str equalsIgnoreCase "Flagged"]
set resp_def_node [getDefinition $graph_id $object_type]
set def_node [get_resp_value $resp_def_node "definition_node"]
if {$returnFields} {
set resp_object [convert_graph_node $graph_node $def_node $fields]
set externalProps [java::new ArrayList]
set returnBody [$fields contains "body"]
if {$returnBody == 1} {
$externalProps add "body"
}
set returnOldBody [$fields contains "oldBody"]
if {$returnOldBody == 1} {
$externalProps add "oldBody"
}
set returnStageIcons [$fields contains "stageIcons"]
if {$returnStageIcons == 1} {
$externalProps add "stageIcons"
}
set externalPropId $content_id
if {$imageMode == 1} {
set externalPropId $content_image_id
}
set bodyResponse [getContentProperties $externalPropId $externalProps]
set check_error [check_response_error $bodyResponse]
if {!$check_error} {
set extValues [get_resp_value $bodyResponse "values"]
set is_extValues_null [java::isnull $extValues]
if {$is_extValues_null == 0} {
set extValuesMap [java::cast Map $extValues]
$resp_object putAll $extValuesMap
}
}
} else {
set resp_object [convert_graph_node $graph_node $def_node]
}
proc_updateLanguageCode $resp_object $graph_node
$resp_object put "identifier" $content_id
if {$isEditMode == 1 && $isLiveState == 1} {
$resp_object put "status" "Draft"
}
set result_map [java::new HashMap]
$result_map put "content" $resp_object
set response_list [create_response $result_map]
return $response_list
}

package require java
