set maxVal [java::call Integer parseInt $max 16]
set words [java::new ArrayList]
set count 1
set startPosition 0
set resultSize 5000
while {$count > 0} {
set nodeList [proc_getWords $language_id $startPosition $resultSize]
puts "batch completed - $startPosition"
set size [$nodeList size]
